package basicPracticeTwo;

public class SwitchCaseOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//winter, spring, summer, autumn
		int winter = 6;
		switch (winter) {
		case 1:
			System.out.println("THE WINTER IS SO HASH");
			
			break;
		case 2:
			System.out.println("THE WINTER IS  NOT  SO HASH");
			
			break;
		case 3:
			System.out.println("THE WINTER  IS NOT COLD HASH");
			
			break;
		case 4:
			System.out.println("THE WINTER  IS COLD BUT IN MOST OF THE TIME IS NOT HASH");
			
			break;
		default: 
			System.out.println(" WINTER IS AMAZING  AND  IS SO BEATIFUL");
			break;
		}
	}

}
